import React, { Component } from 'react';

export default class App extends Component {
  constructor() {
    super();

    this.state = {
      allCountries: [],
    };
  }

  async componentDidMount() {
    //O res vai receber um await do fetch da  url
    const res = await fetch('https://restcountries.eu/rest/v2/all');
    //O json vai ser um await do res em json
    const json = await res.json();
    // dessestruturando o bojeti direto e criando uma array com os elemento necessários
    const allCountries = json.map(({ name, numericCode, flag, population }) => {
      return {
        id: numericCode,
        name,
        flag,
        population,
      };
    });
    this.setState({
      //allCountris foi atribuido diretamente
      allCountries: allCountries,
    });
    //console.log(json); mostra todos os países
  }
  render() {
    const { allCountries } = this.state;
    return (
      <div className="container">
        <h1>React Countries</h1>
      </div>
    );
  }
}
